import React, { useState } from 'react';

const SortingFiltering = ({
  onFilterChange,
  onSortChange,
  availableColumns,
}) => {
  const [selectedColumn, setSelectedColumn] = useState('');
  const [filterValue, setFilterValue] = useState('');

  const handleFilter = () => {
    onFilterChange(selectedColumn, filterValue);
  };

  return (
    <div>
      <div>
        <label>Sort By: </label>
        <select onChange={(e) => setSelectedColumn(e.target.value)}>
          <option value="">Select Column</option>
          {availableColumns.map((column) => (
            <option key={column} value={column}>
              {column}
            </option>
          ))}
        </select>
        <button onClick={() => onSortChange(selectedColumn, 'ASC')}>Asc</button>
        <button onClick={() => onSortChange(selectedColumn, 'DESC')}>
          Desc
        </button>
      </div>
      <div>
        <label>Filter By: </label>
        <select onChange={(e) => setSelectedColumn(e.target.value)}>
          <option value="">Select Column</option>
          {availableColumns.map((column) => (
            <option key={column} value={column}>
              {column}
            </option>
          ))}
        </select>
        <input
          type="text"
          placeholder="Enter Filter Value"
          value={filterValue}
          onChange={(e) => setFilterValue(e.target.value)}
        />
        <button onClick={handleFilter}>Apply Filter</button>
      </div>
    </div>
  );
};

export default SortingFiltering;
