import React from 'react';

const SortingFiltering = ({ onSortChange }) => {
  return (
    <div>
      <div>
        <label>Sort By: </label>
        <select onChange={(e) => onSortChange(e.target.value, 'ASC')}>
          <option value="">Select Column</option>
          {/* Dynamically list columns as options */}
        </select>
        <button onClick={() => onSortChange(selectedColumn, 'ASC')}>Asc</button>
        <button onClick={() => onSortChange(selectedColumn, 'DESC')}>
          Desc
        </button>
      </div>
    </div>
  );
};

export default SortingFiltering;
