import React, { useState } from 'react';

const SortingFiltering = ({
  onFilterChange,
  onSortChange,
  availableColumns,
}) => {
  const [selectedColumn, setSelectedColumn] = useState('');
  const [filterValues, setFilterValues] = useState({});

  const handleFilterChange = (column, value) => {
    setFilterValues((prevFilterValues) => ({
      ...prevFilterValues,
      [column]: value,
    }));
  };

  const handleApplyFilter = () => {
    onFilterChange(filterValues);
  };

  return (
    <div>
      <div>
        <label>Sort By: </label>
        <select onChange={(e) => setSelectedColumn(e.target.value)}>
          <option value="">Select Column</option>
          {availableColumns.map((column) => (
            <option key={column} value={column}>
              {column}
            </option>
          ))}
        </select>
        <button onClick={() => onSortChange(selectedColumn, 'ASC')}>Asc</button>
        <button onClick={() => onSortChange(selectedColumn, 'DESC')}>
          Desc
        </button>
      </div>
      <div>
        {selectedColumn && (
          <div>
            <label>Filter By: {selectedColumn}</label>
            <input
              type="text"
              placeholder={`Enter Filter Value for ${selectedColumn}`}
              value={filterValues[selectedColumn] || ''}
              onChange={(e) =>
                handleFilterChange(selectedColumn, e.target.value)
              }
            />
            <button onClick={handleApplyFilter}>Apply Filter</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default SortingFiltering;
